char flag [] =  "X-MAS{60b0c8849039195e5b0bca3e2631b848d9096fdaa6098c7e87670e0bdcfb9f4a}";//, total 5 + 2 + 64 = 71 characters, 568 bits

int set_positions[569];
int d [1000][6000];

using namespace std;

int rucsac2(int problem_size, int maxg, int * g, int * p)
{
	if(problem_size >= 1000 || maxg >= 6000)
	{
		cout<<"[R2] Warning! Problem size out of bounds!\n";
		return 0;
	}
	
	//clear dp
	for(int i = 0; i < 1000; ++i)
	{
		for(int j = 0; j < 6000; ++j)
		{
			d[i][j] = 0;
		}
	}
	
	for(int i = 1; i <= problem_size; i++)
    {
        for(int j = 1; j <= maxg; ++j)
        {
            d[i][j] = d[i - 1][j];
            if(g[i] <= j)
                d[i][j] = max(d[i][j], d[i - 1][j - g[i]] + p[i]);
        }
    }
    int res = d[problem_size][maxg];
    cout<<"[R2] Max profit: "<<res<<"\n";
	
	return res;
}

int rucsac(int problem_size, int maxg, int * g, int * p, queue <int> & selected, queue <int> & not_selected)
{
	cout<<"[R1] Called! Maxg = "<<maxg<<"\n";
	if(problem_size >= 1000 || maxg >= 6000)
	{
		cout<<"[R1] Warning! Problem size out of bounds!\n";
		return 0;
	}
	
	//clear dp
	for(int i = 0; i < 1000; ++i)
	{
		for(int j = 0; j < 6000; ++j)
		{
			d[i][j] = 0;
		}
	}
	
    for(int i = 1; i <= problem_size; i++)
    {
        for(int j = 1; j <= maxg; ++j)
        {
            d[i][j] = d[i - 1][j];
            if(g[i] <= j)
                d[i][j] = max(d[i][j], d[i - 1][j - g[i]] + p[i]);
        }
    }
    int res = d[problem_size][maxg];
    cout<<"Max profit: "<<res<<"\n";

    int no_selected = 0;
    int cg = maxg;
    int checksum = 0, checkprofit = 0;
    for (int i = problem_size; i > 0 && res > 0; i--)
    {
        if (res == d[i - 1][cg])
        {
            not_selected.push(i);
            continue;
        }
        else
        {
            no_selected++;
            cout<<" "<<i;
            selected.push(i);

            res = res - p[i];
            cg = cg - g[i];

            checksum += g[i];
            checkprofit += p[i];
        }
    }

    //cout<<"\nSelected "<<no_selected<<" items!\n";

    if(checksum > maxg || checkprofit != d[problem_size][maxg])
    {
        cout<<"Extracting solution failed!\n";
        return 0;
    }

	cout<<"\n";
    return no_selected;
}

int main()
{
    //get number of set bits in flag
    int total_bits = 0;
    for(char c : flag)
    {
        int temp = __builtin_popcount(c);
        cout<<c<<" has "<<temp<<" bits set!\n";
        total_bits += temp;
    }

    cout<<"Total set bits: "<<total_bits<<"/568\n";

    for(int i = 0; i <= 71; ++i)
    {
        unsigned char c = flag[i];
        unsigned char mask = 0x80;

        for(int j = 0; j < 8; ++j)
        {
            if(c & (mask >> j))
            {
                //cout<<"Bit "<<i * 8 + j<<" is set!\n";
                set_positions[i * 8 + j] = 1;
            }
			
			else
			{
				set_positions[i * 8 + j] = 0;
			}
        }
    }

    //generate a knapsac case for which the optimal solution has that many elements selected
    int g[569];
    int p[569];
    int maxg;

    queue <int> selected;
    queue <int> not_selected;

    srand(time(0));
    while(1)
    {
        while(!selected.empty()) selected.pop();
        while(!not_selected.empty()) not_selected.pop();

        maxg = rand() % 5000 + 1000;

        for(int i = 1; i <= 568; ++i)
        {
            g[i] = rand() % (maxg / 64) + 1;
            p[i] = rand() % (maxg * 5) + 133;
        }

        if(rucsac(568, maxg, g, p, selected, not_selected) == total_bits)
        {
            cout<<"Maxg = "<<maxg<<"\n";

            cout<<"G[i]: {0, ";
            for(int i = 1; i <= 568; ++i)
                cout<<g[i]<<", ";
            cout<<"0}\n";

            cout<<"P[i]: {0, ";
            for(int i = 1; i <= 568; ++i)
                cout<<p[i]<<", ";
            cout<<"0}\n";
            break;
        }
    }

    //we have the test case here

    int * newg = new int [569];
    int * newp = new int [569];

    for(int i = 1; i < 569; ++i)
    {
        if(set_positions[i - 1])
        {
			if(selected.empty())
			{
				//should not happen
				cout<<"Trying to pop from empty selected Q!\n";
				return -1;
			}
			
            newg[i] = g[selected.front()];
            newp[i] = p[selected.front()];
            selected.pop();
        }

        else
        {
			if(not_selected.empty())
			{
				//should not happen
				cout<<"Trying to pop from empty not-selected Q!\n";
				return -1;
			}
            newg[i] = g[not_selected.front()];
            newp[i] = p[not_selected.front()];
            not_selected.pop();
        }
    }

	//one more check to see if we used all elements
	if(!selected.empty())
	{
		cout<<"Warning selected q not empty!\n";
		return -1;
	}
	if(!not_selected.empty())
	{
		cout<<"Warning non-selected q not empty!\n";
		return -1;
	}
	
    for(int i = 0; i < 569; ++i)
    {
        g[i] = newg[i];
        p[i] = newp[i];
    }

    delete [] newg;
    delete [] newp;

    cout<<"After scrambling:\n";
    cout<<"G[i]: {0, ";
    for(int i = 1; i <= 568; ++i)
        cout<<g[i]<<", ";
    cout<<"0}\n";

    cout<<"P[i]: {0, ";
    for(int i = 1; i <= 568; ++i)
        cout<<p[i]<<", ";
    cout<<"0}\n";
	
	cout<<rucsac2(568, maxg, g, p);

    return 0;
}

